import javax.mail.internet.AddressException;
import javax.mail.internet.InternetAddress;
import com.liferay.portal.kernel.mail.MailMessage;
import com.liferay.mail.service.MailServiceUtil;

try {
	String emailAddress = (String)workflowContext.get("CITY_PROFILE_CONTACT_EMAIL_ADDRESS");
	println "Sending creation mail to cityprofilecontact: "+emailAddress; 
	InternetAddress fromAddress = new InternetAddress(
			"Mayors-ADAPT Site Administrator <no-reply@climate-adapt.eea.europa.eu>");
	InternetAddress toAddress = new InternetAddress(
			emailAddress);
	StringBuffer body = new StringBuffer();
	body.append("<p>Dear City Profile Contact,</p>");
	body.append("<p>Thank you…Welcome to the Mayors Adapt city profile platform. We have set up a template that we would like you to fill in. All of the information shared in this form, will be used to showcase your city’s adaptation work on the EC Climate-ADAPT website. The more information you fill in, the better your city’s impact within the community will be.</p>");
	body.append("<p>We'll get back to you soon with your city profile ready for completion.</p>");
	body.append("<p>For any questions, please don't hesitate to contact us at helpdesk@mayors-adapt.eu</p>");						
	String subject = "Mayors Adapt: welcome to Mayors Adapt city profile platform.";
	MailMessage mailMessage = new MailMessage();
	mailMessage.setTo(toAddress);
	mailMessage.setFrom(fromAddress);
	mailMessage.setSubject(subject);
	mailMessage.setBody(body.toString());
	mailMessage.setHTMLFormat(true);
	MailServiceUtil.sendEmail(mailMessage);

} catch (AddressException e) {
	e.printStackTrace();
}

import javax.mail.internet.AddressException;
